// App.js

import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:5000'); // Update with your server URL

function App() {
  const [documentContent, setDocumentContent] = useState('');
  const editorRef = useRef();

  useEffect(() => {
    // Listen for document updates from the server
    socket.on('update-document', (data) => {
      setDocumentContent(data);
    });

    return () => {
      // Clean up on component unmount
      socket.disconnect();
    };
  }, []);

  const handleInputChange = () => {
    const content = editorRef.current.innerHTML;
    setDocumentContent(content);

    // Send the updated document to the server
    socket.emit('update-document', content);
  };

  const handleFormat = (format) => {
    const selection = window.getSelection();
    const range = selection.getRangeAt(0);
    const span = document.createElement('span');
    span.style[format] = 'true';
    range.surroundContents(span);
    selection.removeAllRanges();
    selection.addRange(range);
    handleInputChange();
    editorRef.current.focus();
  };

  return (
    <div className="App">
      <div className="toolbar">
        <button onClick={() => handleFormat('fontWeight')}>Bold</button>
        <button onClick={() => handleFormat('fontStyle')}>Italic</button>
        <button onClick={() => handleFormat('textDecoration')}>Underline</button>
      </div>
      <div
        ref={editorRef}
        className="editor"
        contentEditable="true"
        dangerouslySetInnerHTML={{ __html: documentContent }}
        onInput={handleInputChange}
      />
    </div>
  );
}

export default App;
