// App.js

import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './App.css';

const socket = io('http://localhost:5000'); // Update with your server URL

function App() {
  const [documentContent, setDocumentContent] = useState('');
  const [username, setUsername] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    if (loggedIn) {
      // Listen for document updates from the server
      socket.on('update-document', (data) => {
        setDocumentContent(data);
      });

      return () => {
        // Clean up on component unmount
        socket.disconnect();
      };
    }
  }, [loggedIn]);

  const handleInputChange = (content) => {
    // Send the updated document to the server
    socket.emit('update-document', content);
  };

  const handleLogin = () => {
    // Simulate authentication by accepting any username
    // In a real-world scenario, replace this with actual authentication logic
    setLoggedIn(true);
  };

  const handleLogout = () => {
    // Handle logout and reset state
    setLoggedIn(false);
    setUsername('');
    setDocumentContent('');
  };

  return (
    <div className="App">
      {loggedIn ? (
        <div className="editor">
          <button onClick={handleLogout}>Logout</button>
          <ReactQuill
            value={documentContent}
            onChange={(content) => handleInputChange(content)}
            modules={{ toolbar: true }}
          />
        </div>
      ) : (
        <div className="login-container">
          <h2>Login</h2>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
